import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import API from '../api';

export default function Signup(){
  const [name,setName]=useState('');
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [err,setErr]=useState(null);
  const nav = useNavigate();

  const submit = async e => {
    e.preventDefault();
    setErr(null);
    try{
      const res = await API.post('/api/auth/signup', { name, email, password });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      nav('/chat');
    }catch(err){
      setErr(err.response?.data?.message || 'Signup failed');
    }
  }

  return (
    <div className="auth-page">
      <div className="auth-card">
        <div className="auth-left">
          <div className="brand">Chat App</div>
          <h2>Create your account</h2>
          <p className="muted">Start chatting with your team and friends.</p>
          <form onSubmit={submit} className="auth-form">
            <div className="field">
              <label>Full name</label>
              <input placeholder="Your full name" value={name} onChange={e=>setName(e.target.value)} />
            </div>
            <div className="field">
              <label>Email address</label>
              <input placeholder="you@example.com" value={email} onChange={e=>setEmail(e.target.value)} />
            </div>
            <div className="field">
              <label>Password</label>
              <input placeholder="Choose a secure password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
            </div>
            <button className="primary" type="submit">Create account</button>
            {err && <div className="error">{err}</div>}
          </form>
        </div>
        <div className="auth-right">
          <div className="illustration">ðŸš€</div>
          <p className="muted">Secure and simple messaging.</p>
          <div style={{marginTop:12}}>Already have an account? <Link to="/login" style={{color:'#fff'}}>Log in</Link></div>
        </div>
      </div>
    </div>
  );
}
