import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import API from '../api';

export default function Login(){
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [err,setErr]=useState(null);
  const nav = useNavigate();

  const submit = async e => {
    e.preventDefault();
    setErr(null);
    try{
      const res = await API.post('/api/auth/login', { email, password });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      nav('/chat');
    }catch(err){
      setErr(err.response?.data?.message || 'Login failed');
    }
  }

  return (
    <div className="auth-container">
      <h2>Login</h2>
      <form onSubmit={submit} className="auth-form">
        <input placeholder="Email address" value={email} onChange={e=>setEmail(e.target.value)} />
        <input placeholder="Password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
        <button type="submit">Log in</button>
        {err && <div className="error">{err}</div>}
      </form>
      <div style={{marginTop:12,display:'flex',gap:8}}>
        <a className="oauth-btn" href="http://localhost:5000/api/auth/google">Login with Google</a>
        <a className="oauth-btn" href="http://localhost:5000/api/auth/facebook">Login with Facebook</a>
      </div>
      <div className="auth-footer">Don't have an account? <Link to="/signup">Sign up</Link></div>
    </div>
  );
}
