import React, { useEffect, useState, useRef } from 'react';
import API from '../api';
import MessageList from '../components/MessageList';

export default function Chat(){
  const [users, setUsers] = useState([]);
  const [selected, setSelected] = useState(null);
  const [messages, setMessages] = useState([]);
  const [text, setText] = useState('');
  const me = JSON.parse(localStorage.getItem('user') || 'null');
  const pollingRef = useRef(null);

  useEffect(()=>{
    if (!me) return;
    API.get('/api/users').then(res => setUsers(res.data)).catch(()=>{});
  },[]);

  useEffect(()=>{
    if (!selected) return;
    fetchMessages();
    pollingRef.current = setInterval(fetchMessages, 3000);
    return ()=> clearInterval(pollingRef.current);
  },[selected]);

  const fetchMessages = async () => {
    try{
      const res = await API.get('/api/messages/' + selected._id);
      setMessages(res.data);
    }catch(err){ console.error(err); }
  }

  const send = async e => {
    e.preventDefault();
    if (!text.trim() || !selected) return;
    try{
      await API.post('/api/messages', { to: selected._id, content: text });
      setText('');
      fetchMessages();
    }catch(err){ console.error(err); }
  }

  const logout = ()=>{ localStorage.removeItem('token'); localStorage.removeItem('user'); window.location.reload(); }

  return (
    <div className="chat-page">
      <div className="sidebar">
        <div className="me">Logged in as <strong>{me?.name}</strong></div>
        <button className="logout" onClick={logout}>Logout</button>
        <h4>Users</h4>
        <div className="users">
          {users.filter(u => u._id !== me?._id).map(u => (
            <div key={u._id} className={`user ${selected?._id===u._id?'active':''}`} onClick={()=>setSelected(u)}>
              <div className="avatar">{u.name.split(' ').map(s=>s[0]).slice(0,2).join('').toUpperCase()}</div>
              <div style={{flex:1}}>
                <div style={{fontWeight:600}}>{u.name}</div>
                <small style={{color:'#64748b'}}>{u.email}</small>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="chat-area">
        {selected ? (
          <>
            <div className="chat-header">Chat with {selected.name}</div>
            <MessageList messages={messages} currentUserId={me?._id} />
            <form className="composer" onSubmit={send}>
              <input value={text} onChange={e=>setText(e.target.value)} placeholder="Type a message..." />
              <button type="submit">Send</button>
            </form>
          </>
        ) : (
          <div className="empty">Select a user to start chatting</div>
        )}
      </div>
    </div>
  );
}
