import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Chat from './pages/Chat';

function App(){
  const token = localStorage.getItem('token');
  return (
    <div className="app-root">
      <header style={{width:'100%',padding:'12px 0',textAlign:'center',background:'#007bff',color:'#fff',marginBottom:12}}>
        <h1 style={{margin:0,fontSize:20}}>Chatting App</h1>
      </header>
      <Routes>
        <Route path="/" element={token ? <Navigate to="/chat" /> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login/>} />
        <Route path="/signup" element={<Signup/>} />
        <Route path="/chat" element={<Chat/>} />
      </Routes>
    </div>
  );
}

export default App;
