import React from 'react';

function initials(name){
  if(!name) return '?';
  return name.split(' ').map(s=>s[0]).slice(0,2).join('').toUpperCase();
}

export default function MessageBubble({ m, mine, alignRight = true }){
  const time = new Date(m.createdAt || m.updatedAt || Date.now()).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});
  // If alignRight is false, invert where 'mine' messages appear
  const effectiveMine = alignRight ? mine : !mine;
  return (
    <div className={`message-row ${effectiveMine ? 'mine' : 'theirs'}`}>
      <div className={`message ${effectiveMine ? 'out' : 'in'}`}>
        <div className="bubble-content">
          {!effectiveMine && (
            m.from.avatar ? <img src={m.from.avatar} alt="av" className="avatar-msg"/> : <div className="avatar-msg initials">{initials(m.from.name)}</div>
          )}
          <div className="text-block">
            <div className="content">{m.content}</div>
            <div className="time">{time}</div>
          </div>
        </div>
      </div>
    </div>
  );
}
