import React from 'react';

function initials(name){
  if(!name) return '?';
  return name.split(' ').map(s=>s[0]).slice(0,2).join('').toUpperCase();
}

export default function MessageList({ messages, currentUserId }){
  return (
    <div className="message-list">
      {messages.map(m => {
        const mine = m.from._id === currentUserId || m.from._id === currentUserId;
        const time = new Date(m.createdAt || m.updatedAt || Date.now()).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});
        return (
          <div key={m._id} className={`message-row`}>
            <div className={`message ${mine ? 'out' : 'in'}`}>
              <div style={{display:'flex',alignItems:'center',gap:10}}>
                {!mine && (
                  m.from.avatar ? <img src={m.from.avatar} alt="av" style={{width:36,height:36,borderRadius:8,objectFit:'cover'}} /> : <div className="avatar">{initials(m.from.name)}</div>
                )}
                <div style={{flex:1}}>
                  <div className="meta">{m.from.name}</div>
                  <div className="content">{m.content}</div>
                </div>
              </div>
              <div className="time">{time}</div>
            </div>
          </div>
        );
      })}
    </div>
  );
}
