import React from 'react';
import MessageBubble from './MessageBubble';

export default function MessageList({ messages, currentUserId, alignRight = true }){
  return (
    <div className="message-list">
      {messages.map(m => {
        const mine = m.from._id === currentUserId;
        return <MessageBubble key={m._id} m={m} mine={mine} alignRight={alignRight} />;
      })}
    </div>
  );
}
