import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import API from '../api';

export default function Signup(){
  const [name,setName]=useState('');
  const [email,setEmail]=useState('');
  const [password,setPassword]=useState('');
  const [err,setErr]=useState(null);
  const nav = useNavigate();

  const submit = async e => {
    e.preventDefault();
    setErr(null);
    try{
      const res = await API.post('/api/auth/signup', { name, email, password });
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      nav('/chat');
    }catch(err){
      setErr(err.response?.data?.message || 'Signup failed');
    }
  }

  return (
    <div className="auth-container">
      <h2>Sign up</h2>
      <form onSubmit={submit} className="auth-form">
        <input placeholder="Full name" value={name} onChange={e=>setName(e.target.value)} />
        <input placeholder="Email address" value={email} onChange={e=>setEmail(e.target.value)} />
        <input placeholder="Choose a password" type="password" value={password} onChange={e=>setPassword(e.target.value)} />
        <button type="submit">Create account</button>
        {err && <div className="error">{err}</div>}
      </form>
      <div className="auth-footer">Already have an account? <Link to="/login">Log in</Link></div>
    </div>
  );
}
